<?php

namespace Database\Seeders;

use Illuminate\Database\Seeder;
use TCG\Voyager\Models\Permission;

class PermissionsTableSeeder extends Seeder
{
    /**
     * Auto generated seed file.
     */
    public function run()
    {
        $keys = [
            'browse_admin',
            'browse_bread',
            'browse_database',
            'browse_media',
            'browse_compass',
        ];

        foreach ($keys as $key) {
            Permission::firstOrCreate([
                'key'        => $key,
                'table_name' => null,
            ]);
        }

        Permission::generateFor('menus');
        Permission::generateFor('roles');
        Permission::generateFor('users');
        Permission::generateFor('settings');
        Permission::generateFor('permissions');

        Permission::generateFor('job_types');
        Permission::generateFor('jobs');
        Permission::generateFor('countries');
        Permission::generateFor('states');
        Permission::generateFor('cities');
        Permission::generateFor('job_offers');
        Permission::generateFor('job_applications');
        Permission::generateFor('business');
    }
}
